<?phpclass PreguntasData {	public static $tablename = "preguntas";	public function __construct(){		$this->id = "";		$this->grupo = 0;		$this->fase = "";		$this->pregunta = "";		$this->correcta = 0;		$this->falso = 0;		$this->nombre = "";		$this->respuestA = "";		$this->respuestB = "";		$this->respuestC = "";		$this->respuestD = "";		$this->total = 0;		$this->update_at = "NOW()";		$this->created_at = "NOW()";	}	public function add(){		$sql = "insert into timeline (idcompany, idperson, quien_asigna, prioridad, status, asunto, title, type, date_event, date_pass, porcentaje, created_at) ";		$sql .= "value (".$_SESSION['id_company'].", \"$this->idperson\", \"$this->quien_asigna\", \"$this->prioridad\", 1, \"$this->asunto\", \"$this->title\", \"$this->type\", \"$this->date_event\", \"$this->date_pass\", \"$this->porcentaje\", $this->created_at)";		Executor::doit($sql);	}	public static function delById($id){		$sql = "UPDATE ".self::$tablename." SET is_active = 0 WHERE id=$id";		Executor::doit($sql);	}	public function del(){		$sql = "UPDATE ".self::$tablename." SET is_active = 0 WHERE id=$this->id";		Executor::doit($sql);	}	public function update($id){		$sql = "UPDATE ".self::$tablename." SET body=\"$this->body\", date_pass=NOW(), idejecuta=\"$this->idejecuta\", status=$this->status, update_at=$this->update_at WHERE id=$id";        echo $sql;		Executor::doit($sql);	}		public static function getById($id){		$sql = "SELECT * FROM ".self::$tablename." WHERE grupo = ".$id; 		$query = Executor::doit($sql);		return Model::many($query[0],new PreguntasData());	}	public static function getAll(){		$sql = "SELECT * FROM ".self::$tablename." ORDER BY grupo"; 		$query = Executor::doit($sql);		return Model::many($query[0],new PreguntasData());	}}