<link href="https://cdnjs.cloudflare.com/ajax/libs/summernote/0.8.11/summernote-bs4.css" rel="stylesheet"><script src="https://cdnjs.cloudflare.com/ajax/libs/summernote/0.8.11/summernote-bs4.js"></script><!-- Content Header (Page header) --><section class="content-header">	<h1>		Talento Humano		<small>listado de los documentos</small>	</h1>	<ol class="breadcrumb">		<li><a href="./index.php?view=home"><i class="fa fa-dashboard"></i> Panel de control </a></li>	</ol></section><!-- Main content -->   <div class="container-fluid">        <div class="row">        <div class="col-md-12">    <h1>Enviar Correo</h1> <hr>   <form id="enviar" autocomplete="off">     <div class="form-group row">    <label  class="col-sm-2 col-form-label">Nombre</label>    <div class="col-sm-6">      <input type="text" name="nombre" class="form-control" placeholder="Ingresar su Nombre" required>    </div>    </div>         <div class="form-group row">    <label  class="col-sm-2 col-form-label">Correo</label>    <div class="col-sm-6">      <input type="email" name="correo" class="form-control" placeholder="Ingresar el Correo" required>    </div>    </div>    <div class="form-group row">          <label class="col-sm-2 col-form-label">Asunto</label>        <div class="col-sm-5">    <input type="text" name="asunto" class="form-control" required>    </div>    </div>    <div class="form-group row">          <label class="col-sm-2 col-form-label">Mensaje</label>        <div class="col-sm-10">    <textarea name="mensaje" class="mensaje form-control" rows="10" required></textarea>    </div>    <script>    $('.mensaje').summernote({        placeholder: 'Ingrese el contenido de su mensaje',        tabsize: 2,        height: 250      });    </script>    </div>    <div class="form-group row">    <label class="col-sm-2 col-form-label"></label>    <div class="col-sm-10">      <button type="submit" class="btn btn-primary">Enviar</button>    </div>    </div>   </form>        </div>   </div>    </div>  <script>      $(document).on('submit','#enviar',function(e){   parametros = $(this).serialize();         $.ajax({      url:"send.php",   type:"POST",   data:parametros,   dataType:"JSON",   beforeSend:function()   {       swal({    title: "Enviando Mensaje",    text: "Espere un momento no Cierre la Ventana",    imageUrl: 'img/loader2.gif',    showConfirmButton: false    });     },   success:function(data)   {     swal({    title: data.title,    text:  data.text,    type:  data.type,    timer: 3000,    showConfirmButton: false    });    $('#enviar')[0].reset();    $(".mensaje").summernote("code", "");   }   });      e.preventDefault();  });  </script>
