								echo '<option value="'.$tables->id.'" '.$valor.'>'.$tables->descripcion.'</option>';  							}							echo '</select>';						?>					</label>				</div>			</div>		</div>			<div class="box-body mailbox-messages">			<div class="row">				<div class="col-sm-12">					<div class="alert alert-dismissable alert-info">						<strong><i class="fa fa-bullhorn"></i> Importante...!</strong>						<p> - Los turnos se identifican con los siguientes colores:&nbsp;</br>							<span class="label label-success">D</span>&nbsp;Diurno,&nbsp;							<span class="label label-warning">L</span>&nbsp;Libre,&nbsp;							<span class="label label-danger" >F</span>&nbsp;Falta y							<span class="label label-purple">LT</span>&nbsp;Libre Trabajado						</p>					</div>				</div>			</div>			<div id="content"></div>			<div style="height: 410px; width: 100%; overflow-x: auto;" id="example_length">				<?php					$users = PuestoData::getByAdministrador($lugar);					if(count($users) > 0){						echo '<table id="horario" class="table table-bordered table-hover">';							echo '<tbody>';							// Crea tabla del Personal Administrativo
							foreach($users as $tables) {								echo '<tr>';									echo '<td>';										echo '<small>';											echo $tables['nombre']; // $lugar.' '.$tables['id'].' '.											echo '</br>Ingreso el: '.$tables['startwork']; //$tables['descripcion'].'-'.$tables['cargo']										echo '</small></br>';																				$valor=HorarioData::getByTurnos($tables['servicio'], $tables['id'], $mes, $ano, 2);										$resultado = count($valor);										$dias=1;										echo '<table>';											echo '<tr>';												for ($i = 1; $i <= $total; $i++) {													if($dias == 1)	echo '<td><div align="center"><small><span class="text-green">LUN</span></small></div></td>';													if($dias == 2)	echo '<td><div align="center"><small><span class="text-green">MAR</span></small></div></td>';													if($dias == 3)	echo '<td><div align="center"><small><span class="text-green">MIE</span></small></div></td>';													if($dias == 4)	echo '<td><div align="center"><small><span class="text-green">JUE</span></small></div></td>';													if($dias == 5)	echo '<td><div align="center"><small><span class="text-green">VIE</span></small></div></td>';													if($dias == 6)	echo '<td><div align="center"><small><span class="text-red">SAB</span></small></div></td>';													if($dias == 7)	echo '<td><div align="center"><small><span class="text-red">DOM</span></small></div></td>';
													if($dias == 7) $dias = 1; else $dias++;												}											echo '</tr>';											echo '<tr>';											$iddia=0;
											for ($i = 1; $i <= $total; $i++) {												echo '<td>';
													if($resultado > 0) {														echo '<input type="hidden" id="hiden_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'" name="hiden_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'" value="'.$valor[$i-1]['id'].'-'.$tables['servicio'].'-'.$tables['id'].'-'.$i.'-'.$mes.'-'.$ano.'-'.$valor[$i-1]['turno'].'">';														$iddia=$valor[$i-1]['id'];														if($valor[$i-1]['turno']==1)															$clase = 'btn-success';														else															if($valor[$i-1]['turno']==2)																$clase = 'btn-primary';															else																if($valor[$i-1]['turno']==3)																	$clase = 'btn-warning';																else																	if($valor[$i-1]['turno']==4)																		$clase = 'btn-danger';																	else																		if($valor[$i-1]['turno']==5)																			$clase = 'bg-purple';																		else																			if($valor[$i-1]['turno']==6)																				$clase = 'btn-bitbucket';																			else																				if($valor[$i-1]['turno']==7)																					$clase = 'btn-github';																				else																					if($valor[$i-1]['turno']==8)																						$clase = 'btn-dropbox';																					else																						if($valor[$i-1]['turno']==9)																							$clase = 'btn-foursquare';																						else																							if($valor[$i-1]['turno']==10)																								$clase = 'btn-flickr';																							else																								$clase = 'btn-default';													} else {														echo '<input type="hidden" id="hiden_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'" name="hiden_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'" value="'.$iddia.'-'.$tables['servicio'].'-'.$tables['id'].'-'.str_pad($i, 2, "0", STR_PAD_LEFT).'-'.$mes.'-'.$ano.'-0">';														$clase = 'btn-default';													}													echo '<input type="button" id="button_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'" name="button_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'" class="btn '.$clase.'" onClick="btn_NuevoOnClick('.$iddia.', '.str_pad($i, 2, "0", STR_PAD_LEFT).', '.$mes.', '.$ano.','.$tables['id'].','.$tables['servicio'].',\'button_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'\',\'hiden_'.$tables['servicio'].'_'.$tables['id'].'_'.$i.'\');" value="'.str_pad($i, 2, "0", STR_PAD_LEFT).'"/>';												echo '</td>';											}											echo '</tr>';										echo '</table>';									echo '</td>';								echo '</tr>';							}							echo '</tbody>';						echo '</table>';					}else{						echo '<div class="alert alert-dismissable alert-danger">								<strong><i class="fa fa-bullhorn"></i> Importante...!</strong>								<p> - A&uacute;n no se ha reclutado a ningun agente.</p>							  </div>';					} 				?>			</div>		</div>	</div></section><script>	var element = document.getElementById("sidai");
	element.classList.add("sidebar-collapse");	document.title = "Near Solutions | Horario Administrativos"		function btn_NuevoOnClick(id, dia, mes, ano, agente, servicio, boton, hiden) {		var oculto = document.getElementById(hiden);		var elemento = document.getElementById(boton);		var valor = 0;				if(dia < 10) dia = '0' + dia;		if (elemento.className == "btn btn-default") {			elemento.className = "btn btn-success";			oculto.value = id+"-"+servicio+"-"+agente+"-"+dia+"-"+mes+"-"+ano+"-1";			valor = 1;		}else{			if (elemento.className == "btn btn-success") {				elemento.className = "btn btn-warning";				oculto.value = id+"-"+servicio+"-"+agente+"-"+dia+"-"+mes+"-"+ano+"-3";				valor = 3;			}else{				if (elemento.className == "btn btn-warning") {					elemento.className = "btn btn-danger";					oculto.value = id+"-"+servicio+"-"+agente+"-"+dia+"-"+mes+"-"+ano+"-4";					valor = 4;				}else{					if (elemento.className == "btn btn-danger") {						elemento.className = "btn bg-purple";						oculto.value = id+"-"+servicio+"-"+agente+"-"+dia+"-"+mes+"-"+ano+"-5";						valor = 5;					}else{						elemento.className = "btn btn-default";						oculto.value = id+"-"+servicio+"-"+agente+"-"+dia+"-"+mes+"-"+ano+"-0";						valor = 0;					}				}			}		}				// AÃ±adimos la imagen de carga en el contenedor 		$.ajax({			type: "POST",			url: "ajax/horario.php?id="+id+"&servicio="+servicio+"&agente="+agente+"&dia="+dia+"&mes="+mes+"&ano="+ano+"&valor="+valor,			success: function(data) {				/* Cargamos finalmente el contenido deseado */				console.log("id="+id+"-"+servicio+"-"+agente+"-"+dia+"-"+mes+"-"+ano+"-"+valor);				//sweetAlert('Excelente', data, 'success');				$('#content').fadeIn(1000).html(data); 			}		});	}</script>
